abstract class ExpenseTable {
  static const String table = 'expense';
  static const String id = 'expense_id';
  static const String name = 'expense_name';
  static const String totalValue = 'expense_totalValue';
  static const String paidValue = 'expense_paidValue';
  static const String remainingValue = 'expense_remainingValue';
  static const String paymentDate = 'expense_paymentDate';
  static const String lastIncludeDate = 'expense_lastIncludeDate';
  static const String overdueDate = 'expense_dueDate';
  static const String personName = 'expense_personName';
  static const String frequency = 'expense_frequency';
  static const String typeId = 'expense_typeId';
}
