abstract class ExpenseTable {
  static const String table = 'expense';
  static const String id = 'expense_id';
  static const String name = 'expense_name';
  static const String value = 'expense_value';
  static const String expirationDate = 'expense_expDate';
  static const String personName = 'expense_personName';
  static const String frequency = 'expense_frequency';
  static const String typeId = 'expense_typeId';
  static const String paymentMethodId = 'expense_payMethodId';
}
