abstract class InvoiceTable {
  static const String table = 'invoice';
  static const String id = 'invoice_id';
  static const String value = 'invoice_value';
  static const String iof = 'invoice_iof';
  static const String interest = 'invoice_interest';
  static const String paidValue = 'invoice_paidValue';
  static const String remainingValue = 'invoice_remainingValue';
  static const String paymentDate = 'invoice_paymentDate';
  static const String closeDate = 'invoice_closeDate';
  static const String overdueDate = 'invoice_dueDate';
  static const String cardId = 'invoice_cardId';
}
