abstract class TransactionTable {
  static const String table = 'transactions';
  static const String id = 'transaction_id';
  static const String date = 'transaction_date';
  static const String value = 'transaction_value';
  static const String expenseId = 'transaction_expenseId';
  static const String incomeId = 'transaction_incomeId';
  static const String invoiceId = 'transaction_invoiceId';
}
